import React, { useContext, useState, useEffect } from 'react';
import ContactContext from '../../context/contact/contactContext';

const ContactForm = () => {
  const contactContext = useContext(ContactContext);
  const { addContact, current } = contactContext;

  useEffect(() => {
    if (current !== null) {
      setContact(current);
    } else {
      setContact({ name: '', email: '', phone: '', type: 'personal' });
    }
  }, [current]);

  const [contact, setContact] = useState({
    name: '',
    email: '',
    phone: '',
    type: 'personal'
  });

  const { name, email, phone, type } = contact;

  const onSubmit = e => {
    e.preventDefault();
    contactContext.addContact(contact);
    setContact({ name: '', email: '', phone: '', type: 'personal' });
  };
  const onChange = e =>
    setContact({ ...contact, [e.target.name]: e.target.value });

  return (
    <form onSubmit={onSubmit}>
      <h2 className="text-primary">
        <input
          type="text"
          placeholder="Name"
          name="name"
          value={name}
          onChange={onChange}
        />
        <input
          type="email"
          placeholder="Email"
          name="email"
          value={email}
          onChange={onChange}
        />
        <input
          type="text"
          placeholder="Phone"
          name="phone"
          value={phone}
          onChange={onChange}
        />
        <h5>Contact Type</h5>
        <input
          type="radio"
          name="type"
          id=""
          value="Personal"
          checked={type === 'Personal'}
          onChange={onChange}
        />{' '}
        Personal{' '}
        <input
          type="radio"
          name="type"
          id=""
          value="Professional"
          checked={type === 'Professional'}
          onChange={onChange}
        />
        Professional{' '}
        <div>
          <input
            type="submit"
            value="Add Contact"
            className="btn btn-primary btn-block"
          />
        </div>
      </h2>
    </form>
  );
};

export default ContactForm;
